                    movePowerResult: m.power,
                    moveAccuracyResult: m.accuracy,
                    moveEffectChanceResult: m.effect_chance,
                    moveTypeResult: m.type.name.charAt(0).toUpperCase() + m.type.name.slice(1),
                    currentView: "MoveInfo"
