                } else if(typeColor === 'water' || typeColor === 'ice') {
                    this.setState({
                        borderStyle: {borderColor: 'blue'},
                        colorStyle: {color: 'blue'}
                    })
                } else if(typeColor === 'electric') {
                    this.setState({
                        borderStyle: {borderColor: 'yellow'},
