                    })
                } else if(typeColor === 'grass') {
                    this.setState({
                        borderStyle: {borderColor: 'green'},
                        colorStyle: {color: 'green'}
                    })
                } else if(typeColor === 'poison') {
                    this.setState({
                        borderStyle: {borderColor: 'purple'},
